import { filter, map } from "rxjs/operators";
import { NavigationEnd } from "@angular/router";
export class FunksioneAuction {
  calculateDiff(dateSent) {
    const currentDate = new Date();
    dateSent = new Date(dateSent); // tslint:disable-next-line:max-line-length

    return Math.floor((Date.UTC(dateSent.getFullYear(), dateSent.getMonth(), dateSent.getDate()) - Date.UTC(currentDate.getFullYear(), currentDate.getMonth(), currentDate.getDate())) / (1000 * 60 * 60 * 24));
  }

  getWallet() {
    const user = JSON.parse(localStorage.getItem('user'))['username'];
    const localUser = JSON.parse(localStorage.getItem('users_list')).filter(x => x.username === user);
    return localUser[0]['wallet'];
  }

  changeHeader() {
    const appTitle = this.titleService.getTitle();
    this.router.events.pipe(filter(event => event instanceof NavigationEnd), map(() => {
      const child = this.route.firstChild;

      if (child.snapshot.data['title']) {
        return child.snapshot.data['title'];
      }

      return appTitle;
    })).subscribe(ttl => {
      this.titleService.setTitle(ttl);
    });
    return this.titleService.getTitle();
  }

}
